import './App.css';

import axios from 'axios';
// import React, { Component } from 'react';

import React from 'react';

import { Component } from 'react';

// import { useState } from 'react';
// import Textarea from "react-dropzone-textarea";

import {inference} from './inference.js';
import {columnNames} from './inference.js';
import {modelDownloadInProgress} from './inference.js';
import Chart from "react-google-charts";
import Box from '@mui/material/Box';
import LinearProgress from '@mui/material/LinearProgress';

import { useState } from 'react';
import './App.scss';
import FileUpload from './FileUpload/FileUpload';
import FileList from './FileList/FileList';
import FileProcessing from './FileProcessing';
import Dashboard from './Dashboard';
import './Dashboard.css';

// import {uploadfile} from './uploadfile.js';

// import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus } from '@fortawesome/free-solid-svg-icons'
import './FileUpload/FileUpload.scss'
// import axios from 'axios'



class TextInputArea extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      text: 'Enter text to classify emotion, model trained on English text.',
      data:columnNames,
      latency:0.0,
      downloading:modelDownloadInProgress()
    };
    this.handleChange = this.handleChange.bind(this);
  }


  Dashboard = () => {
    const [textValue, setTextValue] = useState("");
    const handleChange = (e) => {
      const file = e.target.files[0];

      let reader = new FileReader();

      reader.onload = (e) => {
        const file = e.target.result;
        console.log(file);
        setTextValue(file);
      };

      reader.onerror = (e) => alert(e.target.error.name);
      reader.readAsText(file);
    };
    return (
      // <div className="Dashboard">
      <div className="App">
      <div style={{ display: "flex" }}>
        <input type="file" name="input" onChange={handleChange} />
        <textarea
          cols={30}
          rows={20}
          value={textValue}
          onChange={setTextValue}
          style={{ marginTop: 15, width: "50%" }}
        ></textarea>
      </div>
      </div>
    );
  };





  state = {
    // Initially, no file is selected
    selectedFile: null
  }

  // On file select (from the pop up)
  onFileChange = event => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  }

  // On file upload (click the upload button)
  onFileUpload = () => {

    // Create an object of formData
    // const formData = new FormData();

    // // Update the formData object
    // formData.append(
    //   "myFile",
    //   this.state.selectedFile,
    //   this.state.selectedFile.name
    // );

    // Details of the uploaded file
    console.log(this.state.selectedFile);

    // Request made to the backend api
    // Send formData object

    // axios.post("https://file-upload8.p.rapidapi.com/upload", formData, 

    // {
    //   headers: {
    //     "Content-Type": "multipart/form-data",
    //     "x-rapidapi-host": "file-upload8.p.rapidapi.com",
    //     "x-rapidapi-key": "54b5d42c85mshe8beeaee0224812p19c856jsn693dc0130385",
    //   },
    // }
    // );


    // axios.post("https://file-upload8.p.rapidapi.com/upload", {myFile: (this.state.selectedFile,this.state.selectedFile.name)}, 

    // {
    //   headers: {
    //     "Content-Type": "multipart/form-data",
    //     "x-rapidapi-host": "file-upload8.p.rapidapi.com",
    //     "x-rapidapi-key": "67c784071bmsh339dd9d9dfa4776p1995bdjsn2731d6524052",
    //   },
    // }
    // );



    axios.post("http://localhost:3001/upload", {myFile: (this.state.selectedFile,this.state.selectedFile.name)});

  }

  // // File content to be displayed after
  // // file upload is complete
  // fileData = () => {
  //   if (this.state.selectedFile) {
  //     return (
  //       <div>
  //         <h5>File Details:</h5>
  //         <p>File Name: {this.state.selectedFile.name}</p>
  //         <p>File Type: {this.state.selectedFile.type}</p>
  //         <p>
  //           Last Modified:{" "}
  //           {this.state.selectedFile.lastModifiedDate.toDateString()}
  //         </p>
  //       </div>
  //     );
  //   } else {
  //     return (
  //       <div>
  //         <br />
  //         <h5>Choose before Pressing the Upload button</h5>
  //       </div>
  //     );
  //   };
  // }



  componentDidMount() {
    this.timerID = setInterval(
      () => this.checkModelStatus(),
      1000
    );
  }

  checkModelStatus() {
    this.setState({
      downloading: modelDownloadInProgress(),
    });
    if (!this.state.downloading) {
      this.timerID = setInterval(
        () => this.checkModelStatus,
        5000000
      );
    }
  }

  // handleChange(event) {  
  //   inference(event.target.value).then( result => {
  //     this.setState({
  //       text : event.target.value,
  //       data:result[1],
  //       latency:result[0],
  //     });
  //   });

  //   // inference(event.target.value).then( result => {
  //   //   this.setState({
  //   //     text : text => Dashboard.textValue,
  //   //     data:result[1],
  //   //     latency:result[0],
  //   //   });
  //   // });

  // }



  handleChange(event) {  
    inference(event.target.value).then( result => {
      this.setState({
        text : event.target.value,
        data:result[1],
        latency:result[0],
      });
    });

  }



  render() {
    return (
      <div className="App">

      {/* <FileProcessing /> */}

      <header className="App-header">   
      <em>In-Browser Transformer Inference</em>
      <Chart  
        width={'400px'}
        height={'200px'}
        chartType="BarChart"
        data={this.state.data}
        options={{
          chartArea: { width: '40%'},
          colors: ['purple'],
          backgroundColor: '#282c34',
          legend: { 
            textStyle: {color: 'white', fontSize: 10},
            labels: {fontColor:'white'}
          },
          vAxis: {
            textStyle: {
            color: 'white',
            fontSize: 13
          }
          },
          hAxis: {
            minValue: 5,
            maxValue: 50,
            textStyle: {
              color: 'white'
            }
          }
      }}
      />




          <FileProcessing />

          {/* <Dashboard /> */}



          {/* <div>
          <h5>
              File Upload
          </h5>
        <div>
          <input type="file" onChange={this.onFileChange} />
          <button onClick={this.onFileUpload}>Upload!</button>
        </div>
        {this.Dashboard()}
      </div> */}




      {this.state.downloading && 
        <div><font size="2">Downloading model from CDN to browser..</font>
        <Box sx={{ width: '400px' }}>
        <LinearProgress />
        </Box> 
        <p></p>
        </div>
      }


      {/* <textarea rows="8" cols="24" className="App-textarea" name="message" 
       placeholder={this.state.text} autoFocus onChange={this.handleChange}

       >
      </textarea> */}



      {/* <textarea rows="8" cols="24" className="App-textarea"  name="message" 
      //  placeholder={this.state.text} autoFocus // onChange={this.handleChange}
       placeholder={Dashboard.textValue} autoFocus // onChange={this.handleChange}
      // rows="8" cols="24" placeholder={this.state.text}

        // placeholder={this.state.text} autoFocus onChange={this.onFileChange}
        value = {Dashboard.textValue}
        // onChange={e => Dashboard.textarea.onChange(e.target.value)}
        // onChange={Dashboard.setTextValue}
        onChange={this.onFileChange}
        // onDropRead={text => Dashboard.textarea.onChange.value}

        // textareaProps={{
        //   // cols: 24,
        //   // rows: 8,
        //   placeholder: "Drop one text file here..."
        // } }

        // onChange={this.Dashboard}  
        // {this.Dashboard()} 
        ></textarea> */}



       <div><font size="3">Inference Latency {this.state.latency} ms</font></div>
      <div><font size="3">GitHub Repo: <a href="https://github.com/jobergum/browser-ml-inference">browser-ml-inference</a></font></div>
      <div><font size="3">Model was trained on the <a href="https://ai.googleblog.com/2021/10/goemotions-dataset-for-fine-grained.html">GoEmotions</a> dataset.</font></div>

      </header> 


    </div>   
    );





  }


}
export default TextInputArea;










