import {inference} from './inference.js';
import {columnNames} from './inference.js';
import {modelDownloadInProgress} from './inference.js';
import Chart from "react-google-charts";
import Box from '@mui/material/Box';
import LinearProgress from '@mui/material/LinearProgress';

import { useState } from 'react';
import './App.scss';
import FileUpload from './FileUpload/FileUpload';
import FileList from './FileList/FileList';
import FileProcessing from './FileProcessing';
import Dashboard from './Dashboard';
import './Dashboard.css';

// import {uploadfile} from './uploadfile.js';

// import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus } from '@fortawesome/free-solid-svg-icons'
import './FileUpload/FileUpload.scss'
// import axios from 'axios'



class TextInputArea extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      text: 'Enter text to classify emotion, model trained on English text.',
      data:columnNames,
      latency:0.0,
      downloading:modelDownloadInProgress()
    };
    this.handleChange = this.handleChange.bind(this);
  }


  Dashboard = () => {
    const [textValue, setTextValue] = useState("");
    const handleChange = (e) => {
      const file = e.target.files[0];

      let reader = new FileReader();

      reader.onload = (e) => {
        const file = e.target.result;
        console.log(file);
        setTextValue(file);
      };

      reader.onerror = (e) => alert(e.target.error.name);
      reader.readAsText(file);
    };
    return (
      // <div className="Dashboard">
      <div className="App">
      <div style={{ display: "flex" }}>
        <input type="file" name="input" onChange={handleChange} />
        <textarea
          cols={30}
          rows={20}
          value={textValue}
          onChange={setTextValue}
          style={{ marginTop: 15, width: "50%" }}
        ></textarea>
      </div>
      </div>
    );
  };





  // state = {
