  componentDidMount() {
    this.timerID = setInterval(
      () => this.checkModelStatus(),
      1000
    );
  }

  checkModelStatus() {
    this.setState({
      downloading: modelDownloadInProgress(),
    });
    if (!this.state.downloading) {
      this.timerID = setInterval(
        () => this.checkModelStatus,
        5000000
      );
    }
  }

  handleChange(event) {  
    // inference(event.target.value).then( result => {
    //   this.setState({
    //     text : event.target.value,
    //     data:result[1],
    //     latency:result[0],
    //   });
    // });

    inference(event.target.value).then( result => {
      this.setState({
        text : text => this.Dashboard.setValue(text),
        data:result[1],
        latency:result[0],
      });
    });

  }

  render() {
    return (
      <div className="App">

      {/* <FileProcessing /> */}

      <header className="App-header">   
      <em>In-Browser Transformer Inference</em>
      <Chart  
        width={'400px'}
        height={'200px'}
        chartType="BarChart"
        data={this.state.data}
        options={{
          chartArea: { width: '40%'},
          colors: ['purple'],
          backgroundColor: '#282c34',
          legend: { 
            textStyle: {color: 'white', fontSize: 10},
            labels: {fontColor:'white'}
          },
          vAxis: {
            textStyle: {
            color: 'white',
            fontSize: 13
          }
          },
          hAxis: {
            minValue: 5,
            maxValue: 50,
            textStyle: {
              color: 'white'
            }
          }
      }}
      />




          {/* <FileProcessing /> */}

          <Dashboard />




      {this.state.downloading && 
        <div><font size="2">Downloading model from CDN to browser..</font>
        <Box sx={{ width: '400px' }}>
        <LinearProgress />
        </Box> 
        <p></p>
        </div>
      }


      {/* <textarea rows="8" cols="24" className="App-textarea" name="message" 
       placeholder={this.state.text} autoFocus onChange={this.handleChange}

       >
      </textarea> */}



      <textarea rows="8" cols="24" className="App-textarea"  name="message" 
       placeholder={this.state.text} autoFocus onChange={this.handleChange}
      // rows="8" cols="24" placeholder={this.state.text}

        // placeholder={this.state.text} autoFocus onChange={this.onFileChange}
        value = {Dashboard.textarea.value}
        // onChange={e => Dashboard.textarea.onChange(e.target.value)}
        onDropRead={text => Dashboard.textarea.onChange.value}

        // textareaProps={{
        //   // cols: 24,
        //   // rows: 8,
        //   placeholder: "Drop one text file here..."
        // } }

        // onChange={this.Dashboard}  
        // {this.Dashboard()} 
        ></textarea>



       <div><font size="3">Inference Latency {this.state.latency} ms</font></div>
      <div><font size="3">GitHub Repo: <a href="https://github.com/jobergum/browser-ml-inference">browser-ml-inference</a></font></div>
      <div><font size="3">Model was trained on the <a href="https://ai.googleblog.com/2021/10/goemotions-dataset-for-fine-grained.html">GoEmotions</a> dataset.</font></div>

      </header> 


    </div>   
    );





  }


}
export default TextInputArea;










