import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus } from '@fortawesome/free-solid-svg-icons'
import './FileUpload.scss'
import axios from 'axios'

// import React, { useState } from "react";
import { useState } from "react";
// import './Dashboard.css';

const FileUpload = ({ files, setFiles, removeFile }) => {

    const [textValue, setTextValue, state, setState] = useState("");


// function FileUpload ({ files, setFiles, removeFile })  {
    const uploadHandler = (event) => {
        const file = event.target.files[0];
        if(!file) return;
        file.isUploading = true;
        setFiles([...files, file])

        // upload file
        // const formData = new FormData();
        // formData.append(

        //     // "newFile",
        //     file.name,
        //     file,
        //     file.name
        // )

        // axios.post('http://localhost:8080/upload', formData)
        axios.post('/upload', {file: (event.target.files[0], event.target.files[0].name)}
        //     {
        //     headers: {
        //       "Content-Type": "multipart/form-data",
        //     },
        //   }
          )
            .then((res) => {
                file.isUploading = false;
                setFiles([...files, file])
            })
            .catch((err) => {
                // inform the user
                console.error(err)
                removeFile(file.name)
            })


        let reader = new FileReader();

        reader.onload = (e) => {
        const file = e.target.result;
        console.log(file);
        setTextValue(file);
        };

        reader.onerror = (e) => alert(e.target.error.name);
        reader.readAsText(file); 


        componentDidMount= (e) =>  {
            timerID = setInterval(
              () => checkModelStatus(),
              1000
            );
          }

          checkModelStatus= (e) =>  {
            setState({
              downloading: modelDownloadInProgress(),
            });
            if (!state.downloading) {
              this.timerID = setInterval(
                () => this.checkModelStatus,
                5000000
              );
            }
          }


        handleChange= (e) =>  {  
            inference(textValue).then( result => {
                setState({
                text : textValue,
                data:result[1],
                latency:result[0],
              });
            });

          }





    }

    return (
        <>

            {/* <div className="file-card">

                <div className="file-inputs">

                    <input type="file" onChange={uploadHandler} />
                    <button>
                        <i>
                            <FontAwesomeIcon icon={faPlus} />
                        </i>
                        Upload
                    </button>


                </div>

                <p className="main">Supported files</p>
                <p className="info">CSV, TXT, PDF, JPG, PNG</p>

            </div> */}


            <div className="Dashboard">
            {/* <div className="App">  */}
            {/* <div style={{ display: "flex" }}> */}
            <input type="file" name="input" onChange={uploadHandler} />
            <button>
                        <i>
                            <FontAwesomeIcon icon={faPlus} />
                        </i>
                        Upload
                    </button>
            <textarea
                cols={30}
                rows={20}
                value={textValue}
                onChange={setTextValue}
                style={{ marginTop: 15, width: "50%" }}
            ></textarea>
            {/* </div> */}



            <Chart  
                    width={'400px'}
                    height={'200px'}
                    chartType="BarChart"
                    data={state.data}
                    options={{
                    chartArea: { width: '40%'},
                    colors: ['purple'],
                    backgroundColor: '#282c34',
                    legend: { 
                        textStyle: {color: 'white', fontSize: 10},
                        labels: {fontColor:'white'}
                    },
                    vAxis: {
                        textStyle: {
                        color: 'white',
                        fontSize: 13
                    }
                    },
                    hAxis: {
                        minValue: 5,
                        maxValue: 50,
                        textStyle: {
                        color: 'white'
                        }
                    }
                }}
                />

                {state.downloading && 
                        <div><font size="2">Downloading model from CDN to browser..</font>
                        <Box sx={{ width: '400px' }}>
                        <LinearProgress />
                        </Box> 
                        <p></p>
                        </div>
                    }


            </div>




        </>


    )
}

export default FileUpload

