                    </button>


                </div>

                <p className="main">Supported files</p>
                <p className="info">CSV, TXT, PDF, JPG, PNG</p>

            </div> */}


            <div className="Dashboard">
            {/* <div className="App">  */}
            {/* <div style={{ display: "flex" }}> */}
            <input type="file" name="input" onChange={uploadHandler} />
            <button>
                        <i>
                            <FontAwesomeIcon icon={faPlus} />
                        </i>
                        Upload
                    </button>

            <textarea
                cols={30}
                rows={20}
                value={textValue}
                onChange={setTextValue}
                style={{ marginTop: 15, width: "50%" }}
            ></textarea> 
            {/* </div> */}






            <form id='csv-form'>
                <input
                    type='file'
                    accept='.csv'
                    id='csvFile'
                    onChange={(e) => {
                        setCsvFile(e.target.files[0])
                    }}
                >
                </input>
                <br/>
                <button
                    onClick={(e) => {
                        e.preventDefault()
                        if(csvFile)submit()
                    }}
                >
                    Submit
                </button>
                <br/>
                <br/>
                {csvArray.length>0 ? 
                <>
                    <table>
                        <thead>
                            <th>product</th>
                            <th>price</th>
                        </thead>
                        <tbody>
                            {
                                csvArray.map((item, i) => (
                                    <tr key={i}>
                                        <td>{item.product}</td>
                                        <td>{item.price}</td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </> : null}
            </form>



            </div>




