import {columnNames} from './inference.js';
import {modelDownloadInProgress} from './inference.js';
import Chart from "react-google-charts";
import Box from '@mui/material/Box';
import LinearProgress from '@mui/material/LinearProgress';



const Dashboard = () => {
  const [textValue, setTextValue] = useState("");
//   const [data, latency, downloading] = useState("");
  const [latency, setLatency] = useState([]);
  const [data, setData] = useState([]);
  const [downloading, setDownloading] = useState([]);
  const [timerID, setInterval] = useState([]);
//   const [timerID] = useState("");
  const [state, setState] = useState("");
  const [result, setResult] = useState("");


//   setData([...data, columnNames]);
//   setDownloading([...downloading, modelDownloadInProgress]);
