//   downloading=modelDownloadInProgress();

//   componentDidMount=()=> {
//     // [textValue, setTextValue] = useState("");
//    timerID = setInterval(
//       () => this.checkModelStatus(),
//       1000
//     );
//   }

//   checkModelStatus=()=> {
//     this.setState({
//       downloading: modelDownloadInProgress(),
//     });
//     if (!this.state.downloading) {
//       this.timerID = setInterval(
//         () => this.checkModelStatus,
//         5000000
//       );
//     }
//   }

  const handleChange = (e) => {


    setData([...data, columnNames]);
    setDownloading([...downloading, modelDownloadInProgress]);
    setLatency([...latency, 0.0]);
    setInterval([...timerID, 10000]);

    // componentDidMount=()=> {
    //         setInterval([...timerID, checkModelStatus(), 1000]);

    //     };


    // checkModelStatus=()=> {
    //     setLatency([...latency, 0.0]);
    //     setDownloading([...downloading, modelDownloadInProgress]);
    //     if (!downloading) {
    //         setInterval([...timerID, checkModelStatus(), 5000000]);
    //     }
    //   }

    // axios.get('/upload')
    // .then(response => {
    //   // Convert data to CSV format(optional)
    //   const file = response.files[0];
    //   console.log(response)
    // })
    // .catch(error => {
    //   // Handle errors
    //   console.error(error);
    // });


    const file = e.target.files[0];

    let reader = new FileReader();

    reader.onload = (e) => {
      const file = e.target.result;
      console.log(file);
      setTextValue(file);
    };

    reader.onerror = (e) => alert(e.target.error.name);
    reader.readAsText(file);

    inference(textValue).then( //result => {

        setData([...data, inference.result[1]]),
        setLatency([...latency, inference.result[0]])
        // setLatency([...latency, 1000])
        // }
        );




    };











  return (
    <div className="Dashboard">
        {/* <header className="App-header">    */}
            {/* <em>In-Browser Transformer Inference</em> */}

        <Chart  
            width={'400px'}
            height={'200px'}
            chartType="BarChart"
            // data={inference.result[1]}
            data={columnNames}
            options={{
            chartArea: { width: '40%'},
            colors: ['purple'],
            backgroundColor: '#282c34',
            legend: { 
                textStyle: {color: 'white', fontSize: 10},
                labels: {fontColor:'white'}
            },
            vAxis: {
                textStyle: {
                color: 'white',
                fontSize: 13
            }
            },
            hAxis: {
                minValue: 5,
                maxValue: 50,
                textStyle: {
                color: 'white'
                }
            }
        }}
        />


     {/* <div className="App">  */}

        {/* <div>
