            <LinearProgress />
            </Box> 
            <p></p>
            </div>
        }

        </div> 




    <div style={{ display: "flex" }}>
      <input type="file" name="input" onChange={handleChange} />
      <textarea
        cols={30}
        rows={20}
        value={textValue}
        onChange={setTextValue}
        style={{ marginTop: 15, width: "50%" }}
      ></textarea>
    </div>


    {/* <div>
          {downloading()}
        </div> */}

        {/* </header>  */}
    </div>
  );
};

