        <FileUpload files={this.App.files} setFiles={this.App.setFiles}
          removeFile={this.App.removeFile} />
        <FileList files={this.App.files} removeFile={this.App.removeFile} />






      {this.state.downloading && 
        <div><font size="2">Downloading model from CDN to browser..</font>
        <Box sx={{ width: '400px' }}>
        <LinearProgress />
        </Box> 
        <p></p>
        </div>
      }


